import React, {useReducer} from 'react';
import CartContext from './cart-context';

let itemsInitial = {
  items: [],
  totalAmount: 0,
};

const itemsReducer = (state, action) => {

  return itemsInitial;
}

const CartProvider = (props) => {

    const addItemToCartHandler = (item) => {}
    const removeItemFromCartHandler = (item) => {}

    const [itemsState, dispatchItems] = useReducer(itemsReducer, itemsInitial);

    const cartProviderValue = {
        items: itemsInitial.items,
        totalAmount: itemsInitial.totalAmount,
        addItem: addItemToCartHandler, 
        removeItem: removeItemFromCartHandler,
    };

  return (
    //Creating this here instead of managing context in App.js
    <CartContext.Provider value={cartProviderValue}>
        {props.children}
    </CartContext.Provider>
  )
}

export default CartProvider;
