import React, {useContex} from 'react';
import Modal from '../UI/Modal';
import CartContext from '../../store/cart-context';
import classes from './Cart.module.css';

const cartCtx = useConext(CartContext);

const Cart = (props) => {
let cartItemsArray = cartCtx.items;
const cartItems = <ul className={classes['cart-items']}> {cartItemsArray.map(item => <li key={item.id}>{item.name}</li>)} </ul>;
  return (
    <Modal onClose={props.onClose}>
        {cartItems}
        <div className={classes.total}>
            <span>Total Amount </span>
            <span>35.62</span>
        </div> 
        <div className={classes.actions}>
            <button onClick={props.onClose} className={classes['button--alt']}>Close</button>
            <button className={classes.button}>Order</button>
        </div>
    </Modal>
  )
}

export default Cart;
